import bs4
import requests


def get_ebay_price(product_url):
    res = requests.get(product_url)
    res.raise_for_status()
    soup = bs4.BeautifulSoup(res.text, 'html.parser')
    element_name = soup.select('#itemTitle')
    element_price = soup.select('#prcIsum')
    print(element_name[0].text.strip())
    print(element_price[0].text.strip())


